def twosum(nums, target):
    nums.sort()
    for i in range(len(nums) - 1):
        j = i + 1
        while j < len(nums):
            if nums[i] + nums[j] == target:
                return [j, i]
            elif nums[i] + nums[j] < target:
                j += 1
            else:
                break
    return []
