def twosum(nums, target):
    nums_original = nums[:]
    nums.sort()
    for i in range(len(nums)):
        for j in range(i + 1, len(nums)):
            if nums[i] + nums[j] == target:
                return [nums_original.index(nums[i]), nums_original.index(nums[j], i+1)]
    return []
